import React from "react";
import { ListGroup, Card } from "react-bootstrap";

function Todo({ todo, key, index, remove }) {
  function handle() {
    remove(index);
  }
  return (
    <ListGroup>
    <ListGroup.Item key={key} className="todo" onClick={handle}>
      <Card>
        <Card.Body>
          <Card.Title>{todo.title}</Card.Title>
          <Card.Text>{todo.description}</Card.Text>
      {/* {todo.todoTitle} <div className="bi bi-trash"></div> */}
      </Card.Body>
      </Card>
    </ListGroup.Item>
    </ListGroup>
  );
}
export default Todo;
