import React from "react";
import { Container, Row, Col } from "react-bootstrap";
import Todo from "./Todo";
import TodoForm from "./TodoForm";

function Home() {
  const [todos, setTodos] = React.useState([
    {
      text: "Outwork",
      isCompleted: false,
    },
    {
      text: "Out-Improve",
      isCompleted: false,
    },
    {
      text: "Out-Strategize",
      isCompleted: false,
    },
    {
      text: "Outlast",
      isCompleted: false,
    },
  ]);

  const addTodo = (todo, description) => {
    let newItem = {todo: description};
    const newTodos = [...todos, { newItem, isCompleted: false }];
    setTodos(newTodos);
  };
  const removeTodo = (index) => {
    var index = Number(index);
    let temp = [...todos];
    temp.splice(index, 1);
    setTodos(temp);
  };

  return (
    <div className="app">
      <div className="list-group">
        <Container>
          <Row>
            <Col>
              {todos.map((todo, i) => (
                <Todo index={i} key={i} todo={todo} remove={removeTodo} />
              ))}
            </Col>
            <Col>
              <TodoForm addTodo={addTodo} />
            </Col>
          </Row>
        </Container>
      </div>
    </div>
  );
}

export default Home;
