import Button from "@restart/ui/esm/Button";
import React from "react";
import { Collapse, Accordion } from "react-bootstrap";

function Todo({ todo, keyNumber, index, remove }) {
  const [open, setOpen] = React.useState(false);
  function handle() {
    remove(index);
  }
  return (
    <div>
      <Accordion>
        <Accordion.Item eventKey={keyNumber}>
          <Accordion.Header>
            <h4 className="todo-header" onClick={handle}>
              {todo.title}
            </h4>
          </Accordion.Header>

          <Accordion.Body>{todo.description}</Accordion.Body>
        </Accordion.Item>
      </Accordion>
    </div>
  );
}
export default Todo;
