import React from "react";
import { Accordion, Toast, Button, Container, Col, Row } from "react-bootstrap";
import { FaTrashAlt, FaBiohazard } from "react-icons/fa";

function Todo({ todo, keyNumber, index, remove }) {
  const [showConfirm, setShowConfirm] = React.useState(false);
  const toggleShowConfirm = () => setShowConfirm(!showConfirm);
  function handle() {
    remove(index);
  }
  return (
    <div>
      <Accordion defaultActivekey="0" flush>
        <Accordion.Item eventKey={keyNumber}>
          <Accordion.Header>
            <FaTrashAlt className="todo-header" onClick={toggleShowConfirm} />
            <h4> {todo.title}</h4>
          </Accordion.Header>

          <Accordion.Body>{todo.description}</Accordion.Body>
        </Accordion.Item>
      </Accordion>
      <Toast show={showConfirm} onClose={toggleShowConfirm}>
        <Toast.Header>
          <FaBiohazard />
          <strong className="me-auto">Just making sure</strong>
        </Toast.Header>
        <Toast.Body>
          Whoa! Are you sure you want to delete this To Do item?
        </Toast.Body>
        <Container>
          <Row>
            <Col>
              <Button onClick={toggleShowConfirm}>No</Button>
            </Col>
            <Col>
              <Button onClick={handle}>Yes</Button>
            </Col>
          </Row>
        </Container>
      </Toast>
    </div>
  );
}
export default Todo;
