import React from "react";
import { ListGroup, Card } from "react-bootstrap";

function Todo({ todo, key, index, remove }) {
  function handle() {
    remove(index);
  }
  return (
    <Card style={{ width: '18rem' }} key={key} onClick={handle}>
      <Card.Body>
        <Card.Header>{todo.title}</Card.Header>
        <Card.Text>{todo.description}</Card.Text>
        {/* {todo.todoTitle} <div className="bi bi-trash"></div> */}
      </Card.Body>
    </Card>
  );
}
export default Todo;
