import React from "react";
import { Collapse } from "react-bootstrap";

function Todo({ todo, keyNumber, index, remove }) {
  const [open, setOpen] = React.useState(false);
  function handle() {
    remove(index);
  }
  return (
    <div>
      <Button
        onClick={() => setOpen(!open)}
        aria-controls="description-collapse"
        aria-expanded={open}
