import Button from "@restart/ui/esm/Button";
import React from "react";
import { Collapse, Accordion } from "react-bootstrap";

function Todo({ todo, keyNumber, index, remove }) {
  const [open, setOpen] = React.useState(false);
  function handle() {
    remove(index);
  }
  return (
    <div>
      <Accordion>

        <Accordion.Item eventKey={keyNumber}>
        <Accordion.Header onClick={handle}>
      <Button
        onClick={() => setOpen(!open)}
        aria-controls="description-collapse"
        aria-expanded={open}
      >
        {todo.title}
      </Button>
      </Accordion.Header>
      <Collapse in={open}>
        <Accordion.Body>
        <div id="description-collapse">{todo.description}</div>
        </Accordion.Body>
      </Collapse>
      </Accordion.Item>
      </Accordion>
    </div>
